import React, { useState } from 'react';
import Picture from '../../assets/Img/picture.png';
import MovingComponent from 'react-moving-text';
import * as S from './styles';
export default function Banner() {
  const AnimationsForChaining = ['fadeInFromRight'];

  const AnimationChain = () => {
    const [animationIndex, setAnimationIndex] = useState(0);
    const [animationType, setAnimationType] = useState(
      AnimationsForChaining[0],
    );

    const handleChainAnimation = () => {
      setCounter(animationIndex + 1);
      setAnimationType(selectedItems[animationIndex + 1]);
    };
    return (
      <S.Body>
        <S.ContainerBody>
          <S.ContainerPicture>
            <S.Frame>
              <S.Picture src={Picture} alt="foto" srcset="" />
            </S.Frame>
          </S.ContainerPicture>
          <S.ContainerInformations>
            <S.TextWelcome>Seja bem vind@</S.TextWelcome>
            <S.TextName>Yasmin Vieira de Almeida</S.TextName>
            <MovingComponent
              type="fadeInFromRight"
              duration="900ms"
              delay="index * 200ms"
              direction="normal"
              timing="ease-in-out"
              iteration="infinite"
              fillMode="forwards"
            >
              Desenvolvedora Front-End
            </MovingComponent>

            <S.TextInfo type="fadeInFromRight">
              Desenvolvedora Front-End
            </S.TextInfo>
            <S.ContainerArray>
              <S.ArrayInfo>
                <span>Infos</span> {'\u007B'}
                <div>
                  Cargo: <span>Estagio,</span>
                </div>
                <div>
                  Empresa: <span>Grupo Energisa</span>
                </div>
                {'\u007D'}
              </S.ArrayInfo>
            </S.ContainerArray>
          </S.ContainerInformations>
        </S.ContainerBody>
      </S.Body>
    );
  };
}
