import React from 'react';
import * as S from './styles';
import Animals from '../../assets/video/animals.gif';
import Calculator from '../../assets/video/calculator.gif';
import CatsAndDogs from '../../assets/video/catsAndDogs.gif';
export default function Projects() {
  const [galery, setGalery] = React.useState([
    {
      id: 1,
      name: 'Calculator',
      image: [<img src={Calculator} alt="" srcset="" />],
      default: '',
    },
    {
      id: 2,
      name: 'CatsAndDogs',
      image: [<img src={CatsAndDogs} alt="" srcset="" />],
      default: '',
    },
    {
      id: 3,
      name: 'Animals',
      image: [<img src={Animals} alt="" srcset="" />],
      default: '',
    },
  ]);
  const setImagem = (e, id) => {
    const { src } = e.target;
    const newGalery = galery.map((item) => {
      if (item.id === id) {
        item.default = src;
      }
      return item;
    });
  };
  return (
    <>
      <S.Body>
        <S.TextTitle>Projetos | Meu portf√≥lio</S.TextTitle>
        <S.SectionProject>
          <div>
            <div>
              <h3>Projeto de uma calculadora Versatil</h3>
              <a href="">
                <img src={Calculator} alt="" srcset="" />
              </a>
            </div>
            <p>
              O projeto foi realizado focando no desenvolvimento de duas
              calculadores, uma espec√≠fica para o calculo do teorema de
              pitagoras e uma calculadora comum.
              <ul>
                <li>React</li>
              </ul>
            </p>
          </div>
          <div>
            <div>
              <h3>Projeto de uma calculadora Versatil</h3>
              <a href="">
                <img src={CatsAndDogs} alt="" srcset="" />
              </a>
            </div>
            <p>
              O projeto foi realizado com o apoio do cuso da plataforma
              VaiNaWeb, foi utilizado:
              <ul>
                <li>React</li>
                <li>API externo</li>
              </ul>
            </p>
          </div>
          <div>
            <h3>Um dos Primeiros projetos realizados</h3>
            <div>
              <a href="https://yasminalmeida.github.io/Animals/">
                <img src={Animals} alt="" srcset="" />
              </a>
            </div>
            <p>
              O projeto foi realizado com o apoio do cuso da plataforma
              Origamid, foi utilizado:
              <ul>
                <li>Javascripst</li>
                <li>Html</li>
                <li>Css</li>
              </ul>
            </p>
          </div>
        </S.SectionProject>
      </S.Body>
    </>
  );
}
