import styled from 'styled-components';
import Banner from '../../assets/Img/banner.png';

export const ContainerAbout = styled.section`
  margin-top: 100px;
  background-image: url(${Banner});
  background-repeat: no-repeat;
  background-size: cover;
`;
export const ContainerInfo = styled.section`
  display: grid;
  grid-template-columns: 1fr;
  justify-items: center;
`;
export const FormationExtra = styled.section``;
export const SectionAbout = styled.section`
  display: grid;
  grid-template-columns: 400px 1fr;
  justify-items: center;
`;
export const ContainerIdiomas = styled.div``;
export const ListaIdiomas = styled.li``;
export const Title = styled.h3`
  font-size: 1.075rem;
  color: #8f8f8f;
  text-transform: uppercase;
  position: relative;
`;
export const Picture = styled.img`
  max-width: 250px;
  border-radius: 4rem;
`;
export const BckgroundPicture = styled.div`
  aspect-ratio: 1/1;
  border-radius: 2rem;
  background: linear-gradient(35deg, transparent, #f8f8, transparent);
  display: grid;
  transform: rotate(18deg);
`;
export const Frame = styled.figure`
  border-radius: 2rem;
  transform: rotate(343deg);
  background: #f8f8;
  border-radius: 4rem;
`;
export const ContainerPicture = styled.div`
  width: max-content;
  border-radius: 4rem;
  display: grid;
  place-items: center;
`;
