import styled from 'styled-components';

export const ContainerHeader = styled.div`
  background-color: #111;
  position: fixed;

  width: 100%;
`;
export const Navigation = styled.nav`
  display: flex;
  justify-content: flex-end;
  margin-bottom: 10px;
  /* position: absolute; */
  right: 0;
`;
export const ContainerLink = styled.ul`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 10px;
  list-style: none;
`;
export const List = styled.li`
  padding: 10px;
  a {
    font-size: 1.05rem;
    color: #f8f8;
    text-decoration: none;
    position: relative;
    display: inline-block;
  }
  p::after {
    content: '';
    display: block;
    height: 2px;
    width: 0px;
    background: #ffff;
    margin-top: 4px;
    transition: 0.3s;
    position: absolute;
  }
`;
