import styled from 'styled-components';
import Idiomas from '../../assets/svg/idiomas.svg';
export const ContainerAbout = styled.section`
  margin-top: 100px;
`;
export const ContainerInfo = styled.section`
  display: grid;
  grid-template-columns: 1fr;
  justify-items: center;
`;
export const FormationExtra = styled.section``;
export const ContainerIdiomas = styled.div``;
export const ListaIdiomas = styled.li``;
export const TitleIdioma = styled.h3`
  font-size: 0.875rem;
  line-height: 1.4;
  color: #8f8f8f;
  text-transform: uppercase;
  margin-bottom: 30px;
  position: relative;

  &::before {
    content: '';
    display: block;
    width: 130px;
    height: 100px;
    background-image: ${Idiomas};
    position: absolute;
  }
`;
